import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom/client';
import axios from 'axios';

function App() {
  const [text, setText] = useState("");
  const [confessions, setConfessions] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchConfessions();
  }, []);

  const fetchConfessions = async () => {
    try {
      const res = await axios.get("/api/confessions");
      setConfessions(res.data);
    } catch (err) {
      setError("‚ùå L·ªói t·∫£i d·ªØ li·ªáu");
    }
  };

  const submitConfession = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      await axios.post("/api/confessions", { text });
      setText("");
      fetchConfessions();
    } catch (err) {
      alert("Kh√¥ng th·ªÉ g·ª≠i l·ªùi th√∫ t·ªôi.");
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial" }}>
      <h1>üïµÔ∏è T∆∞·ªùng Th√∫ T·ªôi ·∫®n Danh</h1>
      <form onSubmit={submitConfession}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows="3"
          cols="50"
          placeholder="Vi·∫øt l·ªùi th√∫ t·ªôi t·∫°i ƒë√¢y..."
        />
        <br />
        <button type="submit">G·ª≠i</button>
      </form>
      <hr />
      <h2>L·ªùi th√∫ t·ªôi g·∫ßn ƒë√¢y</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div style={{ maxHeight: "400px", overflowY: "auto" }}>
        {confessions.map((c) => (
          <div key={c._id} style={{ marginBottom: 10 }}>
            <p>{c.text}</p>
            <small>{new Date(c.createdAt).toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
