import React, { useEffect, useState } from 'react';
import axios from 'axios';

import ReactDOM from 'react-dom/client';

const API_URL = process.env.NODE_ENV === 'development' 
  ? 'http://localhost:5000' 
  : ''; // Empty for production

const EMOTES = ['like', 'haha', 'sad', 'wow'];
const EMOTE_ICONS = {
  like: '‚ù§Ô∏è',
  haha: 'üòÇ',
  sad: 'üò¢',
  wow: 'üòÆ'
};

function App() {
  const [text, setText] = useState('');
  const [confessions, setConfessions] = useState([]);
  const [comments, setComments] = useState({});
  const [newComments, setNewComments] = useState({});

  useEffect(() => {
    fetchConfessions();
  }, []);

  // const fetchConfessions = async () => {
  //   try {
  //     const res = await axios.get('/api/confessions');
  //     setConfessions(res.data);

  //     // Fetch comments for each confession
  //     const allComments = {};
  //     for (let c of res.data) {
  //       const commentRes = await axios.get(`/api/comments/${c._id}`);
  //       allComments[c._id] = commentRes.data;
  //     }
  //     setComments(allComments);
  //   } catch (error) {
  //     console.error("Failed to fetch confessions:", error);
  //   }
  // };

  // const submitConfession = async (e) => {
  //   e.preventDefault();
  //   if (!text.trim()) return;
  //   await axios.post('/api/confessions', { text });
  //   setText('');
  //   fetchConfessions();
  // };

  // const reactToConfession = async (id, type) => {
  //   await axios.post(`http://localhost:5000/api/confessions/${id}/react`, { type });
  //   fetchConfessions();
  // };

  // const submitComment = async (confessionId) => {
  //   const text = newComments[confessionId];
  //   if (!text?.trim()) return;
  //   await axios.post(`http://localhost:5000/api/comments`, { confessionId, text });
  //   setNewComments({ ...newComments, [confessionId]: '' });
  //   fetchConfessions();
  // };

  const fetchConfessions = async () => {
    try {
      const [confessionsRes, commentsRes] = await Promise.all([
        axios.get(`${API_URL}/api/confessions`),
        axios.get(`${API_URL}/api/comments`)
      ]);
      
      setConfessions(confessionsRes.data);
      
      // Organize comments by confession ID
      const commentsMap = commentsRes.data.reduce((acc, comment) => {
        if (!acc[comment.confessionId]) {
          acc[comment.confessionId] = [];
        }
        acc[comment.confessionId].push(comment);
        return acc;
      }, {});
      
      setComments(commentsMap);
    } catch (error) {
      console.error("Fetch error:", error);
      // You could add state to show error to user
      // setError("Failed to load confessions");
    }
  };
  
  // Enhanced API call functions
  const reactToConfession = async (id, type) => {
    try {
      await axios.post(`${API_URL}/api/confessions/${id}/react`, { type });
      fetchConfessions(); // Refresh data
    } catch (error) {
      console.error("Reaction error:", error);
    }
  };
  
  const submitComment = async (confessionId) => {
    const text = newComments[confessionId];
    if (!text?.trim()) return;
    
    try {
      await axios.post(`${API_URL}/api/comments`, { 
        confessionId, 
        text 
      });
      setNewComments({ ...newComments, [confessionId]: '' });
      fetchConfessions();
    } catch (error) {
      console.error("Comment error:", error.response?.data || error.message);
    }
  };


  

  return (
    <div className="App" style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>üïµÔ∏è Anonymous Confession Wall</h1>

      <form onSubmit={submitConfession}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows="3"
          cols="50"
          placeholder="Write something..."
        />
        <br />
        <button type="submit">Confess</button>
      </form>

      <hr />

      <h2>Recent Confessions</h2>
      {confessions.map((c) => (
        <div key={c._id} style={{ border: '1px solid #ccc', padding: 10, marginBottom: 20 }}>
          <p>{c.text}</p>
          <small>{new Date(c.createdAt).toLocaleString()}</small>
          <div style={{ marginTop: 10 }}>
            {EMOTES.map((type) => (
              <button key={type} onClick={() => reactToConfession(c._id, type)} style={{ marginRight: 5 }}>
                {EMOTE_ICONS[type]} {c.reactions?.[type] || 0}
              </button>
            ))}
          </div>
          <div style={{ marginTop: 10 }}>
            <strong>Comments:</strong>
            <ul>
              {(comments[c._id] || []).map((com) => (
                <li key={com._id}>
                  {com.text} <small>({new Date(com.createdAt).toLocaleString()})</small>
                </li>
              ))}
            </ul>
            <input
              type="text"
              placeholder="Write a comment..."
              value={newComments[c._id] || ''}
              onChange={(e) => setNewComments({ ...newComments, [c._id]: e.target.value })}
            />
            <button onClick={() => submitComment(c._id)}>Send</button>
          </div>
        </div>
      ))}
    </div>
  );
}


const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export default App;
