import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom/client';

const API_BASE = process.env.NODE_ENV === 'development' 
  ? 'http://localhost:5000' 
  : '';

const EMOTES = ['like', 'haha', 'sad', 'wow'];
const EMOTE_ICONS = {
  like: '‚ù§Ô∏è',
  haha: 'üòÇ',
  sad: 'üò¢',
  wow: 'üòÆ'
};

function App() {
  const [text, setText] = useState('');
  const [confessions, setConfessions] = useState([]);
  const [comments, setComments] = useState({});
  const [newComments, setNewComments] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchConfessions();
  }, []);

  const fetchConfessions = async () => {
    try {
      const [confessionsRes, commentsRes] = await Promise.all([
        axios.get(`${API_BASE}/api/confessions`),
        axios.get(`${API_BASE}/api/comments`)
      ]);
      
      setConfessions(confessionsRes.data);
      
      // Organize comments by confession ID
      const commentsMap = commentsRes.data.reduce((acc, comment) => {
        if (!acc[comment.confessionId]) {
          acc[comment.confessionId] = [];
        }
        acc[comment.confessionId].push(comment);
        return acc;
      }, {});
      
      setComments(commentsMap);
      setError(null);
    } catch (err) {
      console.error("Fetch error:", err);
      setError("Failed to load confessions. Please try again.");
    }
  };

  const submitConfession = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    
    try {
      await axios.post(`${API_BASE}/api/confessions`, { text });
      setText('');
      await fetchConfessions();
    } catch (err) {
      console.error("Submission error:", err);
      setError("Failed to submit confession.");
    }
  };

  const reactToConfession = async (id, type) => {
    try {
      await axios.post(`${API_BASE}/api/confessions/${id}/react`, { type });
      await fetchConfessions();
    } catch (err) {
      console.error("Reaction error:", err);
      setError("Failed to add reaction.");
    }
  };

  const submitComment = async (confessionId) => {
    const text = newComments[confessionId];
    if (!text?.trim()) return;
    
    try {
      await axios.post(`${API_BASE}/api/comments`, { 
        confessionId, 
        text 
      });
      setNewComments({ ...newComments, [confessionId]: '' });
      await fetchConfessions();
    } catch (err) {
      console.error("Comment error:", err);
      setError("Failed to post comment.");
    }
  };

  return (
    <div className="App" style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>üïµÔ∏è Anonymous Confession Wall</h1>
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      <form onSubmit={submitConfession}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows="3"
          cols="50"
          placeholder="Write something..."
        />
        <br />
        <button type="submit">Confess</button>
      </form>

      <hr />

      <h2>Recent Confessions</h2>
      {confessions.length === 0 ? (
        <p>No confessions yet. Be the first to confess!</p>
      ) : (
        confessions.map((c) => (
          <div key={c._id} style={{ border: '1px solid #ccc', padding: 10, marginBottom: 20 }}>
            <p>{c.text}</p>
            <small>{new Date(c.createdAt).toLocaleString()}</small>
            <div style={{ marginTop: 10 }}>
              {EMOTES.map((type) => (
                <button 
                  key={type} 
                  onClick={() => reactToConfession(c._id, type)} 
                  style={{ marginRight: 5 }}
                >
                  {EMOTE_ICONS[type]} {c.reactions?.[type] || 0}
                </button>
              ))}
            </div>
            <div style={{ marginTop: 10 }}>
              <strong>Comments:</strong>
              <ul>
                {(comments[c._id] || []).map((com) => (
                  <li key={com._id}>
                    {com.text} <small>({new Date(com.createdAt).toLocaleString()})</small>
                  </li>
                ))}
              </ul>
              <input
                type="text"
                placeholder="Write a comment..."
                value={newComments[c._id] || ''}
                onChange={(e) => setNewComments({ ...newComments, [c._id]: e.target.value })}
                style={{ marginRight: 5 }}
              />
              <button onClick={() => submitComment(c._id)}>Send</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export default App;
