import axios from 'axios';

import ReactDOM from 'react-dom/client';


const EMOTES = ['like', 'haha', 'sad', 'wow'];
const EMOTE_ICONS = {
  like: '‚ù§Ô∏è',
  haha: 'üòÇ',
  sad: 'üò¢',
  wow: 'üòÆ'
};

function App() {
  const [text, setText] = useState('');
  const [confessions, setConfessions] = useState([]);
  const [comments, setComments] = useState({});
  const [newComments, setNewComments] = useState({});

  useEffect(() => {
    fetchConfessions();
  }, []);

  // const fetchConfessions = async () => {
  //   const res = await axios.get('/api/confessions');
  //   setConfessions(res.data);

  //   // Fetch comments for each confession
  //   const allComments = {};
  //   for (let c of res.data) {
  //     const commentRes = await axios.get(`http://localhost:5000/api/comments/${c._id}`);
  //     allComments[c._id] = commentRes.data;
  //   }
  //   setComments(allComments);
  // };

  // const submitConfession = async (e) => {
  //   e.preventDefault();
  //   if (!text.trim()) return;
  //   await axios.post('/api/confessions', { text });
  //   setText('');
  //   fetchConfessions();
  // };

  // const reactToConfession = async (id, type) => {
  //   await axios.post(`http://localhost:5000/api/confessions/${id}/react`, { type });
  //   fetchConfessions();
  // };

  // const submitComment = async (confessionId) => {
  //   const text = newComments[confessionId];
  //   if (!text?.trim()) return;
  //   await axios.post(`http://localhost:5000//api/comments`, { confessionId, text });
  //   setNewComments({ ...newComments, [confessionId]: '' });
  //   fetchConfessions();
  // };

  const fetchConfessions = async () => {
    try {
      const res = await axios.get('/api/confessions');
      setConfessions(res.data);
    } catch (error) {
      if (error.response) {
        // L·ªói t·ª´ server (status code 500 tr·ªü l√™n)
        console.error('L·ªói t·ª´ server:', error.response.data);
        alert('ƒê√£ x·∫£y ra l·ªói tr√™n server. Vui l√≤ng th·ª≠ l·∫°i sau.');
      } else if (error.request) {
        // L·ªói khi kh√¥ng nh·∫≠n ƒë∆∞·ª£c ph·∫£n h·ªìi t·ª´ server
        console.error('Kh√¥ng nh·∫≠n ƒë∆∞·ª£c ph·∫£n h·ªìi t·ª´ server:', error.request);
