        alert('C√≥ l·ªói x·∫£y ra. Vui l√≤ng th·ª≠ l·∫°i.');
      }
    }
  };
  

  return (
    <div className="App" style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>üïµÔ∏è Anonymous Confession Wall</h1>

      <form onSubmit={submitConfession}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows="3"
          cols="50"
          placeholder="Write something..."
        />
        <br />
        <button type="submit">Confess</button>
      </form>

      <hr />

      <h2>Recent Confessions</h2>
      {confessions.map((c) => (
        <div key={c._id} style={{ border: '1px solid #ccc', padding: 10, marginBottom: 20 }}>
          <p>{c.text}</p>
          <small>{new Date(c.createdAt).toLocaleString()}</small>
          <div style={{ marginTop: 10 }}>
            {EMOTES.map((type) => (
              <button key={type} onClick={() => reactToConfession(c._id, type)} style={{ marginRight: 5 }}>
